def is_prime(n): 
    if n == 2: 
        return True
    if n < 2 or n % 2 == 0: 
        return False
    # Check if a number is divisible by other numbers
    i = 3
    while i * i <= n: 
        if n % i == 0: 
            return False
        i += 2
    return True

